# https://leetcode.cn/problems/unique-substrings-in-wraparound-string/

class Solution:
    @staticmethod
    def findSubstringInWraproundString(p: str) -> int:
        res = set()
        i = 0
        while i < len(p):
            j = i
            k = 0
            while j < len(p):
                if ord(p[j]) - ord(p[i]) == k:
                    res.add(p[i:j + 1])
                else:
                    break
                j += 1
                if j < len(p):
                    if ord(p[j]) - ord(p[j - 1]) == -25:
                        k += -26
                k += 1
            i += 1
        # print(res)
        # print(len(res))
        return len(res)


Solution.findSubstringInWraproundString("")
